    @SuppressWarnings("resource")
    @Test
    public void testHashCode() {
        try {
            DeviceListOptions devicelistoptions1 = new DeviceListOptions(Integer.valueOf(4), Long.valueOf(32),
                                                                         Order.getDefault(),
                                                                         "d1e81488-2c80-4b38-88f7-584c4de3e694", null,
                                                                         null);
            DeviceListOptions devicelistoptions2 = new DeviceListOptions(Integer.valueOf(4), Long.valueOf(32),
                                                                         Order.getDefault(),
                                                                         "d1e81488-2c80-4b38-88f7-584c4de3e694", null,
                                                                         null);
            assertNotNull(devicelistoptions1);
            assertNotNull(devicelistoptions2);
            assertNotSame(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2.hashCode(), devicelistoptions1.hashCode());
            int hashCode = devicelistoptions1.hashCode();
            for (int i = 0; i < 5; i++) {
                assertEquals(hashCode, devicelistoptions1.hashCode());
            }
        } catch (Exception exception) {
            fail(exception.getMessage());
        }
    }

    /**
     * Tests the equals method.
     */
    @SuppressWarnings("resource")
    @Test
    public void testEquals() {
        try {
            DeviceListOptions devicelistoptions1 = new DeviceListOptions(Integer.valueOf(-57), Long.valueOf(-67),
                                                                         Order.getDefault(),
                                                                         "524eef4a-14f2-4334-9c36-ba80d164713e", null,
                                                                         null);
            DeviceListOptions devicelistoptions2 = new DeviceListOptions(Integer.valueOf(-57), Long.valueOf(-67),
                                                                         Order.getDefault(),
                                                                         "524eef4a-14f2-4334-9c36-ba80d164713e", null,
                                                                         null);
            DeviceListOptions devicelistoptions3 = new DeviceListOptions(Integer.valueOf(-111), Long.valueOf(-91),
                                                                         Order.getDefault(),
                                                                         "95528636-933a-4330-9a51-c8a7d18436db", null,
                                                                         null);
            assertNotNull(devicelistoptions1);
            assertNotNull(devicelistoptions2);
            assertNotNull(devicelistoptions3);
            assertNotSame(devicelistoptions2, devicelistoptions1);
            assertNotSame(devicelistoptions3, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions1, devicelistoptions2);
            assertEquals(devicelistoptions1, devicelistoptions1);
            assertFalse(devicelistoptions1.equals(null));
            assertNotEquals(devicelistoptions3, devicelistoptions1);
        } catch (Exception exception) {
            fail(exception.getMessage());
        }
    }
