    /**
     * Tests the hashCode method.
     */
    @SuppressWarnings("resource")
    @Test
    public void testHashCode() {
        try {
            DeviceListOptions devicelistoptions1 = new DeviceListOptions(Integer.valueOf(126), Long.valueOf(67),
                                                                         Order.getDefault(),
                                                                         "e139bf42-c8b3-4b61-871e-efe360e4edea", null,
                                                                         null);
            DeviceListOptions devicelistoptions2 = new DeviceListOptions(Integer.valueOf(126), Long.valueOf(67),
                                                                         Order.getDefault(),
                                                                         "e139bf42-c8b3-4b61-871e-efe360e4edea", null,
                                                                         null);
            assertNotNull(devicelistoptions1);
            assertNotNull(devicelistoptions2);
            assertNotSame(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2.hashCode(), devicelistoptions1.hashCode());
            int hashCode = devicelistoptions1.hashCode();
            for (int i = 0; i < 5; i++) {
                assertEquals(hashCode, devicelistoptions1.hashCode());
            }
        } catch (Exception exception) {
            fail(exception.getMessage());
        }
    }

    /**
     * Tests the equals method.
     */
    @SuppressWarnings("resource")
    @Test
    public void testEquals() {
        try {
            DeviceListOptions devicelistoptions1 = new DeviceListOptions(Integer.valueOf(-84), Long.valueOf(101),
                                                                         Order.getDefault(),
                                                                         "91eb1344-2dd5-4e3b-8238-dbaaa6be2a6e", null,
                                                                         null);
            DeviceListOptions devicelistoptions2 = new DeviceListOptions(Integer.valueOf(-84), Long.valueOf(101),
                                                                         Order.getDefault(),
                                                                         "91eb1344-2dd5-4e3b-8238-dbaaa6be2a6e", null,
                                                                         null);
            DeviceListOptions devicelistoptions3 = new DeviceListOptions(Integer.valueOf(-109), Long.valueOf(53),
                                                                         Order.getDefault(),
                                                                         "4db7a43a-c28a-4cc4-b190-f82042ad0548", null,
                                                                         null);
            assertNotNull(devicelistoptions1);
            assertNotNull(devicelistoptions2);
            assertNotNull(devicelistoptions3);
            assertNotSame(devicelistoptions2, devicelistoptions1);
            assertNotSame(devicelistoptions3, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions2, devicelistoptions1);
            assertEquals(devicelistoptions1, devicelistoptions2);
            assertEquals(devicelistoptions1, devicelistoptions1);
            assertFalse(devicelistoptions1.equals(null));
            assertNotEquals(devicelistoptions3, devicelistoptions1);
        } catch (Exception exception) {
            fail(exception.getMessage());
        }
    }
